INSERT INTO `oms`.`data_constellation` (`id`, `name`, `datetime`, `date`, `all_luck`, `color`, `health`, `love`, `money`, `number`, `QFriend`, `summary`, `work_luck`, `create_time`) VALUES ('61', '白羊座', '2019年01月13日', '20190113', '40%', '粉红', '60%', '40%', '20%', '7', '白羊座', '今天心里充满了干劲，元气满满的一天。感觉有很多事情要做。但是要注意别把这份干劲花在网购、娱乐上了。另外今天脾气会有些大，与另一半相处时不要太自我了。', '20%', '2019-01-13');
INSERT INTO `oms`.`data_constellation` (`id`, `name`, `datetime`, `date`, `all_luck`, `color`, `health`, `love`, `money`, `number`, `QFriend`, `summary`, `work_luck`, `create_time`) VALUES ('62', '金牛座', '2019年01月13日', '20190113', '40%', '蓝色', '60%', '40%', '20%', '6', '白羊座', '今天自己会不想做事，内心又会特别急，注意下自己的脾气。今天比较适宜吃些补气的食物，忌讳吃油炸上火的东西。今天也会特别想和好朋友。另一半交流。', '20%', '2019-01-13');
INSERT INTO `oms`.`data_constellation` (`id`, `name`, `datetime`, `date`, `all_luck`, `color`, `health`, `love`, `money`, `number`, `QFriend`, `summary`, `work_luck`, `create_time`) VALUES ('63', '双子座', '2019年01月13日', '20190113', '40%', '粉红', '50%', '40%', '20%', '7', '白羊座', '也是适合聚会、约会的一天。但是对待另一半时要注意一下自己任性的毛病，不要挑剔。', '40%', '2019-01-13');
INSERT INTO `oms`.`data_constellation` (`id`, `name`, `datetime`, `date`, `all_luck`, `color`, `health`, `love`, `money`, `number`, `QFriend`, `summary`, `work_luck`, `create_time`) VALUES ('64', '巨蟹座', '2019年01月13日', '20190113', '40%', '奶绿', '70%', '20%', '20%', '9', '天蝎座', '今天一开始自己也是充满活力的，但是后来会因为与另一半、好朋友发生不愉快瞬间心情变差。部分人会因为家庭问题或是朋友聚会推翻自己之前定的执行计划。', '40%', '2019-01-13');
INSERT INTO `oms`.`data_constellation` (`id`, `name`, `datetime`, `date`, `all_luck`, `color`, `health`, `love`, `money`, `number`, `QFriend`, `summary`, `work_luck`, `create_time`) VALUES ('65', '狮子座', '2019年01月13日', '20190113', '40%', '橙色', '60%', '20%', '20%', '1', '狮子座', '需要耐心的一天。今天的脾气还是很急，遇事沟通又很不顺利。今天要注意保存自己的电子产品/文件，被安排临时加班的人一定要确认好工作内容。', '20%', '2019-01-13');
INSERT INTO `oms`.`data_constellation` (`id`, `name`, `datetime`, `date`, `all_luck`, `color`, `health`, `love`, `money`, `number`, `QFriend`, `summary`, `work_luck`, `create_time`) VALUES ('66', '处女座', '2019年01月13日', '20190113', '60%', '青色', '60%', '60%', '60%', '8', '天蝎座', '很容易上火的一天。今天自己火气会比较大，建议吃些滋阴的食物。今天部分人也会因为钱或是家庭的问题和伴侣、家人闹不愉快。', '40%', '2019-01-13');
INSERT INTO `oms`.`data_constellation` (`id`, `name`, `datetime`, `date`, `all_luck`, `color`, `health`, `love`, `money`, `number`, `QFriend`, `summary`, `work_luck`, `create_time`) VALUES ('67', '天秤座', '2019年01月13日', '20190113', '40%', '黄色', '70%', '40%', '40%', '5', '摩羯座', '今天要留心与好朋友、伴侣的关系处理。今天对方会比较强势，自己的心情也会急躁，一言不合就开怼。今天也有人会因为财务状况或是突发的工作取消出行计划。', '40%', '2019-01-13');
INSERT INTO `oms`.`data_constellation` (`id`, `name`, `datetime`, `date`, `all_luck`, `color`, `health`, `love`, `money`, `number`, `QFriend`, `summary`, `work_luck`, `create_time`) VALUES ('68', '天蝎座', '2019年01月13日', '20190113', '40%', '粉红', '60%', '20%', '20%', '7', '双鱼座', '今天会很有干劲去处理事情，但是要注意不要爆脾气。部分人会因为好友、伴侣或是合作伙伴损失一笔钱财，或是和对方闹矛盾。今天也有人会安排工作出差。', '40%', '2019-01-13');
INSERT INTO `oms`.`data_constellation` (`id`, `name`, `datetime`, `date`, `all_luck`, `color`, `health`, `love`, `money`, `number`, `QFriend`, `summary`, `work_luck`, `create_time`) VALUES ('69', '射手座', '2019年01月13日', '20190113', '60%', '淡绿', '70%', '40%', '20%', '3', '射手座', '今天会特别想要参加聚会，也有人会有愉快的活动需要参加。今天自己心情还不错，对朋友也会很迁就。', '40%', '2019-01-13');
INSERT INTO `oms`.`data_constellation` (`id`, `name`, `datetime`, `date`, `all_luck`, `color`, `health`, `love`, `money`, `number`, `QFriend`, `summary`, `work_luck`, `create_time`) VALUES ('70', '摩羯座', '2019年01月13日', '20190113', '40%', '暗红色', '60%', '20%', '40%', '7', '白羊座', '今天比较适合宅在家里。另外今天火气会比较旺，注意吃些滋阴的食物。另外也有人会安排走亲戚或是邀请他人来家里做客。', '40%', '2019-01-13');
INSERT INTO `oms`.`data_constellation` (`id`, `name`, `datetime`, `date`, `all_luck`, `color`, `health`, `love`, `money`, `number`, `QFriend`, `summary`, `work_luck`, `create_time`) VALUES ('71', '水瓶座', '2019年01月13日', '20190113', '40%', '粉红色', '60%', '40%', '40%', '2', '白羊座', '今天部分人会觉得比较无聊，总要找一些事打发时间。也有人会很积极的安排聚会或是出去玩。今天在人际方面的花销也挺大。', '20%', '2019-01-13');
