mapper.select:
select *
from data_trade_record

    admin_id = #{adminId}

        and `type` = #{type}


        and `date` >= #{startDay}


        and `date` <= #{endDay}


order by date desc,id desc


select count(id)
from data_trade_record
where (end_money - start_money) >= 0
  and type = #{type}
  and `date` <= #{date}


select IFNULL(SUM(income_value), 0)
from data_trade_record
where date >= #{beginDate}
  and date <= #{endDate}

select (SUM(income_value) / 100) as incomeValue, DATE_FORMAT(`date`, '%Y-%m-%d') as `date` from data_trade_record
and type = #{type}
GROUP BY DATE_FORMAT(`date`, '%Y-%m-%d') order by DATE_FORMAT(`date`, '%Y-%m-%d') desc limit 30
