mapper.select:
select *
from data_trade_record

    uid = #{uid}

        and `type` = #{type}



        and `date` >= #{startDay}




        and `date` <= #{endDay}



order by date desc, id desc


select count(id)
from data_trade_record
where (end_money - start_money) >= 0
  and type = #{type}
  and `date` <= #{date}
  and uid = #{uid}


select IFNULL(SUM(income_value), 0)
from data_trade_record
where date >= #{beginDate}
  and date <= #{endDate}
  and uid = #{uid}

select (SUM(income_value)) as incomeValue, DATE_FORMAT(`date`, '%Y-%m-%d') as `date`, currency from data_trade_record
uid = #{uid}
and type = #{type}
GROUP BY DATE_FORMAT(`date`, '%Y-%m-%d'), currency order by DATE_FORMAT(`date`, '%Y-%m-%d') desc limit 30

select distinct date
from data_trade_record
where uid = #{uid}
order by date desc
limit #{limit}
