mapper.update:
update system_notice
set is_del = #{isDel}
where id IN

    #{id}

update system_notice
set read_status = #{readStatus}
where id IN

    #{id}

mapper.delete:
delete from system_notice
where id IN

    #{id}

mapper.select:
select *
from system_notice
where is_del = 0
  and read_status = #{readStatus}
  and uid = #{uid}

select *
from system_notice
where uid = #{uid}

    and read_status = #{readStatus}


    and is_del = 0


    and is_del = #{isDel}

order by create_time desc
